const text = ($json.message?.text || "").trim().toLowerCase();
const rexTokenCmd = /^token\s+(0x[a-fA-F0-9]{40})$/;
const rexWallet    = /^0x[a-fA-F0-9]{40}$/;

let type    = "ask";
let address = null;

// Jika pengguna kirim 'token 0x....'
if (rexTokenCmd.test(text)) {
  type    = "token";
  address = text.match(rexTokenCmd)[1];
}
// Jika hanya address EVM biasa tanpa 'token'
else if (rexWallet.test(text)) {
  type    = "wallet";
  address = text;
}
// Jika /help
else if (["/help", "help"].includes(text)) {
  type = "help";
}

else if (["/start", "help"].includes(text)) {
  type = "help";
}

return [{
  json: {
    type,
    address,
    chat_id: $json.message.chat.id,
    query: type === "ask" ? text : null
  }
}];

const { dex = {}, address, chat_id } = $json;

/* â”€â”€ 1. Validasi jaringan â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
const chainId = (dex.chainId || dex.chain || "").toString().toLowerCase();

if (!chainId.includes("sei")) {
  return [{
    json: {
      text:
        "âŒ Sorry, Seiva only supports token analysis on the *Sei Network*.\n" +
        "The address you provided appears to be on a different chain.",
      chat_id,
      parse_mode: "Markdown"
    }
  }];
}

/* â”€â”€ 2. Helper & format functions â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
const fmt = (n, d = 2) => Number(n).toLocaleString("id-ID", {
  maximumFractionDigits: d,
  minimumFractionDigits: d,
});
const formatBig = (n) => {
  const num = Number(n);
  if (num >= 1e9) return fmt(num / 1e9, 2) + " B";
  if (num >= 1e6) return fmt(num / 1e6, 2) + " M";
  if (num >= 1e3) return fmt(num / 1e3, 2) + " K";
  return fmt(num, 2);
};
const pad = (label, value) => `â€¢ ${label.padEnd(13)}: ${value}`;

/* â”€â”€ 3. Core data â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
const token     = dex.baseToken || {};
const name      = token.name   || "Unknown";
const symbol    = token.symbol || "";
const contract  = token.address || "-";

const price      = Number(dex.priceUsd || 0);
const marketcap  = Number(dex.marketCap || 0);
const volume24h  = Number(dex.volume?.h24 || 0);
const liquidity  = Number(dex.liquidity?.usd || 0);

let change1h = dex.priceChangePct?.h1
            ?? dex.priceChange?.h1
            ?? dex.delta?.h1
            ?? 0;
change1h = Number(change1h);
const changeStr = (change1h > 0 ? "+" : "") + change1h.toFixed(2) + "%";

/* â”€â”€ 4. Social links â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
const socials  = dex.info?.socials  || [];
const websites = dex.info?.websites || [];

const tw  = socials.find(s => s.type?.toLowerCase() === "twitter")?.url  || "-";
const tg  = socials.find(s => s.type?.toLowerCase() === "telegram")?.url || "-";
const web = websites[0]?.url || "-";

/* â”€â”€ 5. Swap link â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
const swapLink = contract !== "-"
  ? `https://dragonswap.app/swap?inputCurrency=SEI&outputCurrency=${contract}`
  : "https://dragonswap.app";

/* â”€â”€ 6. Build message â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
const lines = [
  pad("Price",       `$${fmt(price, 7)}`),
  pad("Marketcap",   `$${formatBig(marketcap)}`),
  pad("Change 1h",   changeStr),
  pad("Volume 24h",  `$${formatBig(volume24h)}`),
  pad("Liquidity",   `$${formatBig(liquidity)}`)
].join("\n");

const text =
`ðŸ“Š *${name}* (${symbol})
\`\`\`
${contract}
\`\`\`
${lines}

ðŸŒ *Socials*
${pad("Twitter",  tw !== "-" ? `[Twitter](${tw})`   : "-")}
${pad("Telegram", tg !== "-" ? `[Telegram](${tg})` : "-")}
${pad("Website",  web !== "-" ? `[Website](${web})` : "-")}

ðŸ”— *Swap on DragonSwap:* [Open on DragonSwap](${swapLink})`;

/* â”€â”€ 7. Return to Telegram â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
return [{
  json: {
    text,
    chat_id,
    parse_mode: "Markdown"
  }
}];

